import React from "react";
import "./style.css";
import CardBtn from "../CardBtn";
import beth from "../Images";

// an array of all information for the cards, loop thru and then display into the cards
const Characters = [
    {img: beth},
    {img: 0},
    {img: 0},
    {img: 0},
    {img: 0},
    {img: 0}
];

function Card(props) {
    return (
        <div
            className="card"
            style={{
                backgroundImage: props.image ? `url(${props.image})` : "none"
            }}
        >
            {!props.image && <i className="fa fa-spinner fa-spin" aria-hidden="true" />}
            {/* <CardBtn
                style={{ opacity: props.image ? 1 : 0 }}
                onClick={props.handleBtnClick}
                data-value="pass"
            /> */}
            <CardBtn
                style={{ opacity: props.image ? 1 : 0 }}
                onClick={props.handleBtnClick}
                data-value="pick"
            />
        </div>
    );
}

export default Card;
