import React from "react";
import "./style.css";
import CardBtn from "../CardBtn";
import beth from "../Images/Beth.jpg";
import morty from "../Images/Morty.jpg";
import rick from "../Images/Rick.jpg";
import jerry from "../Images/Jerry.jpg";
import summer from "../Images/Summer.jpg";

// an array of all information for the cards, loop thru and then display into the cards
const characters = [
    {img: beth},
    {img: morty},
    {img: rick},
    {img: jerry},
    {img: summer}
];

function Card(props) {
    return (
        <div
            className="card"
            style={{
                backgroundImage: rick ? `url(${props.image})` : "none"
            }}
        >
            {!props.image && <i className="fa fa-spinner fa-spin" aria-hidden="true" />}
            {/* <CardBtn
                style={{ opacity: props.image ? 1 : 0 }}
                onClick={props.handleBtnClick}
                data-value="pass"
            /> */}
            <CardBtn
                style={{ opacity: props.image ? 1 : 0 }}
                onClick={props.handleBtnClick}
                data-value="pick"
            />
        </div>
    );
}

export default Card;
